one = 5two = 7three = 0def convert(number):    output = ""    num = [1, 4, 5, 9, 10, 40, 50, 90,        100, 400, 500, 900, 1000]    sym = ["I", "IV", "V", "IX", "X", "XL",        "L", "XC", "C", "CD", "D", "CM", "M"]    i = 12         while number:        div = number // num[i]        number %= num[i]         while div:            output += sym[i]            div -= 1        i -= 1    return(output)def roman():    global one, two, three    three = one + two    one = convert(one)    two = convert(two)    three = convert(three)roman()print(one + " + " + two+ " = " + three)